import Link from 'next/link';
import Head from 'next/head';

const PageLink = props => (<>
<Link href={props.href}><a>
<div className='sidebarLink'>ðŸ¡ª {props.name}
</div>
</a></Link>
<style jsx>{`

  .sidebarLink {
    background-color: rgba(50,50,150,0.2);
    margin: auto;
  }

  a {
    text-decoration: none;
    transition-duration: 0.2s;
  }

  /* unvisited link */
  a:link {
    color: #ccf;
  }
  
  /* visited link */
  a:visited {
    color: #ccf;
  }
  
  /* mouse over link */
  a:hover {
    color: #88f;
    font-size: 120%;
  }
  
  /* selected link */
  a:active {
    color: #88f;
  }
`}</style>
</>)


const Sidebar = props => (
    
    <div className='wrapperStyle'>
        <Head>
          <link rel="stylesheet" type="text/css" href="/static/style/wrapper.css"/>
        </Head>
    <div className='sideBarStyle'>
        <h3>Pages</h3>
        
      
           <PageLink href='/' name='Home' />
           <PageLink href='/projects' name='Projects' />
           <PageLink href='/posts' name='Posts' />
           
      
         </div>
    <div className='contentStyle'>
        {props.children}
    </div>
    

    </div>
);

export default Sidebar;
