import Link from 'next/link';

const wrapperStyle = {
    color: "#ccc",
    fontFamily: "'Raleway', sans-serif"
};

const sideBarStyle = {
    width: '240px',
    position: 'fixed',
    top: 0,
    left: 0,
    height: '100%',
    borderStyle: 'solid',
    borderWidth: '0px',
    overflowY: 'auto',
    overflowX: 'hidden',
    padding: '5px'
};

const contentStyle = {
    marginLeft: '250px'
};

<style jsx>{`
  /* unvisited link */
  a:link {
    color: red;
  }
  
  /* visited link */
  a:visited {
    color: green;
  }
  
  /* mouse over link */
  a:hover {
    color: hotpink;
  }
  
  /* selected link */
  a:active {
    color: blue;
  }
`}</style>

const Sidebar = props => (
    
    <div style={wrapperStyle}>
        <style jsx global>{`
          body {
            background-image: url("/static/media/image/back.jpg")
          }
            
        `}</style>
    <div style={sideBarStyle}>
        <h3>Pages</h3>
        
       <ul>
           <li><Link href='/'><a>Home</a></Link></li>
           <li><Link href='/projects'><a>Projects</a></Link></li>
           <li>content</li>
       </ul>
         </div>
    <div style={contentStyle}>
        {props.children}
    </div>
    </div>
);

export default Sidebar;
