import Link from 'next/link';
import Head from 'next/head';

const wrapperStyle = {
    color: "#ccc",
    fontFamily: "'Raleway', sans-serif"
};

const sideBarStyle = {
    width: '240px',
    position: 'fixed',
    top: 0,
    left: 0,
    height: '100%',
    borderStyle: 'solid',
    borderWidth: '0px',
    overflowY: 'auto',
    overflowX: 'hidden',
    padding: '5px'
};

const contentStyle = {
    marginLeft: '250px'
};

const PageLink = props => (<>
<Link href={props.href}><a>
<div className='sidebarLink'>ðŸ¡ª {props.name}
</div>
</a></Link>
<style jsx>{`

  .sidebarLink {
    background-color: rgba(50,50,150,0.2);
    margin: auto;
  }

  a {
    text-decoration: none;
    transition-duration: 0.2s;
  }

  /* unvisited link */
  a:link {
    color: #ccf;
  }
  
  /* visited link */
  a:visited {
    color: #ccf;
  }
  
  /* mouse over link */
  a:hover {
    color: #88f;
    font-size: 120%;
  }
  
  /* selected link */
  a:active {
    color: #88f;
  }
`}</style>
</>)


const Sidebar = props => (
    
    <div style={wrapperStyle}>
        <Head>
          <link rel="stylesheet" type="text/css" href="/static/style/wrapper.css"/>
        </Head>
    <div style={sideBarStyle}>
        <h3>Pages</h3>
        
      
           <PageLink href='/' name='Home' />
           <PageLink href='/projects' name='Projects' />
           <PageLink href='/posts' name='Posts' />
           
      
         </div>
    <div style={contentStyle}>
        {props.children}
    </div>
    

    </div>
);

export default Sidebar;
